mapper.select:
select tMarketBoard.*, tUser.userName from tMarketBoard left join tUser on tMarketBoard.nUserSeq=tUser.userSeq
where tMarketBoard.sBoardTitle like concat('%',#{searchWord},'%')
order by nBoardSeq desc limit #{startNum}, #{perPage}

mapper.update:
update tMarketBoard
set nBoardViewCount=nBoardViewCount+1
where nBoardSeq=#{boardSeq}
